import {App} from'./App.js';
import './App.css';
import {useRef, useState} from 'react';

export function Menu({settings})
{
  const createButton = () => {
    settings.setShow(!settings.show);
  }
  const editButton = () => {
    settings.setEdit(!settings.edit);
  }
  const isEditSticky = () =>{
    settings.setEditSticky(!settings.editSticky);

  }
  const deleteButton = () => {
  const updatedArray=settings.stickies.filter((element) => element.select !== true);
  settings.setStickies(updatedArray);
    //console.log(updatedArray);
  }

  const SortStickies=() => {
    const sortedArray=[...settings.stickies].sort((a,b) => a.color > b.color ? 1: -1);
    settings.setStickies(sortedArray);
    // console.log(sortedArray);
  }

  const saveCurrentBoard=() => {

   var current={};
   current.title=settings.myTitle;
   current.description=settings.myDescription;
   current.stickies=settings.stickies;
    settings.boards.push(current);
    settings.setBoards(current);
    console.log(settings.boards);

    //clear all
    const clearedArray=settings.stickies.filter((element) => element.id < 0);
    settings.setStickies(clearedArray);
    settings.myTitle='';
    settings.myDescription='';
   console.log(settings.boards);

}


    return(
        <div className='Menu'>
         <label className='menuLabel'>Menu</label>
          <div className='menu1'>
            <label className='options'>Board Options:</label> 
            <button id='finish' onClick={editButton}>Edit Board</button>
            <button id='no'onClick={settings.viewBoards}>View Boards</button>
            <button id='finish' onClick={saveCurrentBoard}>Save Boards</button>
          </div>
          <div className='menu2'>
            <label className='options'>Task options:</label>

            <button id='finish' onClick={createButton}>Create Task</button>

            <button id='no' onClick={isEditSticky}> Edit Task</button>
            <button id='finish'onClick={deleteButton}>Delete Task</button>
            <button id='no'> Import Task</button>
            <button id='no' >Export Task</button>
            <button id='finish' onClick={SortStickies}>Group Task by color</button>
          </div>
      </div>
      );
}
