import './App.css';
import {Menu} from './Menu.js';
//import {useState} from 'react';
import {useRef} from 'react';

function App() {

  const board=new Array();
  let sticky={id: null, text:"",color:"" ,select:"",vote:0};
  var currentText;
  var  currentColor;
  var indexId=1;

  const [show, setShow] = useState(false);
  const [back, setBack]=useState(true);
  const [okay, setOkay]=useState(true);

  const [edit, setEdit]=useState(false);
  const [editBack,setEditBack]= useState(true);
  const [editOk, setEdiitOk]=useState(true);

  const [gray, setGray]=useState(false);
  const [yellow, setYellow]=useState(false);
  const [blue, setBlue]=useState(false);
  const [pink, setPink]=useState(false);
  const [purple, setPurple]=useState(false);
  const [green, setGreen]=useState(false);

  //const [message, setMessage]= useState('');
 const ref=useRef(null);

  {/*const handleMessage = event =>{
    currentText = event.target.value;
    setMessage(currentText);
    console.log(currentText);
    //console.log(sticky.text);
  }*/}
  consthandleMessage = event => {
    currentText=ref.current.value;
    console.log(currentText);
  }


  const settings={
    show,
    setShow,
    edit,
    setEdit
  };

  const clickBack= () =>{
    setBack(back);
    setShow(!show);
  }
  const ClickOkay = () => {
      setOkay(okay);
      setShow(!show);
      //id,text,color,
      if(gray){
        currentColor='rgb(128, 128, 128)';
      }else if(yellow){
        currentColor='rgb(255, 255, 0)';
      }else if(blue){
        currentColor='rgb(0, 255, 255)';
      }else if(pink)
      {
        currentColor='rgb(255, 192, 203)';
      }else if(purple){
        currentColor='rgb(128, 0, 128)';
      }else if(green){
        currentColor='rgb(0, 128, 0)';
      }

   sticky.id=indexId++;
   //console.log(currentText);
   sticky.text=currentText;
   sticky.color=currentColor;
   board.push(sticky);
   console.log(board);

  }
  const  ClickEditBack = () => {
    setEditBack(editBack);
    setEdit(!edit);
  }
  const clickEditOkay = () => {
    setEdiitOk(editOk);
    setEdit(!edit);
  }

const clickGray = () => {
  setGray(!gray);

  if(gray)
  {
    setGray(!gray);
  }
  if(yellow){
    setYellow(!yellow);
  }
  if(blue){
    setBlue(!blue);
  }
  if(pink){
    setPink(!pink);
  }
  if(purple){
    setPurple(!purple);
  }
  if(green){
    setGreen(!green);
  }
}
const clickYellow = () =>{
  setYellow(!yellow);

  if(gray)
  {
    setGray(!gray);
  }
  if(blue){
    setBlue(!blue);
  }
  if(pink){
    setPink(!pink);
  }
  if(purple){
    setPurple(!purple);
  }
  if(green){
    setGreen(!green);
  }
}
const clickBlue= () =>{
  setBlue(!blue);

  if(gray)
  {
    setGray(!gray);
  }
  if(yellow){
    setYellow(!yellow);
  }
  if(pink){
    setPink(!pink);
  }
  if(purple){
    setPurple(!purple);
  }
  if(green){
    setGreen(!green);
  }
}
const clickPink = () => {
  setPink(!pink);

  if(gray)
  {
    setGray(!gray);
  }
  if(yellow){
    setYellow(!yellow);
  }
  if(blue){
    setBlue(!blue);
  }
  if(purple){
    setPurple(!purple);
  }
  if(green){
    setGreen(!green);
  }
}
const clickPurple = () => {
  setPurple(!purple);

  if(gray)
  {
    setGray(!gray);
  }
  if(yellow){
    setYellow(!yellow);
  }
  if(blue){
    setBlue(!blue);
  }
  if(pink){
    setPink(!pink);
  }
  if(green){
    setGreen(!green);
  }
}
const clickGreen = () => {
  setGreen(!green);

  if(gray)
  {
    setGray(!gray);
  }
  if(yellow){
    setYellow(!yellow);
  }
  if(blue){
    setBlue(!blue);
  }
  if(pink){
    setPink(!pink);
  }
  if(purple){
    setPurple(!purple);
  }
}



  return (

    <div className='App'>
     <Menu settings={settings}/>
     {
           back && show && <div>
          <div className='addMenu'>
            <textarea className='textAreaAdd' value={message} onChange={handleMessage}></textarea>
            <div className='flexContainer2'>
              <button id='gray' onClick={clickGray}></button>
              <button id='yellow' onClick={clickYellow}></button>
              <button id='blue' onClick={clickBlue}></button>
              <button id='pink' onClick={clickPink}></button>
              <button id='purple' onClick={clickPurple}></button>
              <button id='green' onClick={clickGreen}></button>
          </div>
          <div className='flexContainer3'>
            <button onClick={ClickOkay}>OK</button>
            <button onClick={clickBack}>BACK</button>

          </div>
        </div>

         </div>
   }
   {
      edit && <div>
       <div className='editMenu'>
       <p className='editText'>Edit title:</p>
       <div><textarea></textarea></div>
       <p className='editText'>Edit description:</p>
       <div><textarea></textarea></div>
       <div className='flexContainer1'>
         <button onClick={clickEditOkay}>OK</button>
         <button onClick={ClickEditBack}>BACK</button>
       </div>
     </div>
     </div>
   }
   {
            gray && back && show && <div>
          <div className='addMenu'>
            <textarea id='gray' className='textAreaAdd'></textarea>
            <div className='flexContainer2'>
              <button id='gray' onClick={clickGray}></button>
              <button id='yellow' onClick={clickYellow}></button>
              <button id='blue'onClick={clickBlue}></button>
              <button id='pink' onClick={clickPink}></button>
              <button id='purple' onClick={clickPurple}></button>
              <button id='green'  onClick={clickGreen}></button>
          </div>
          <div className='flexContainer3'>
            <button onClick={ClickOkay}>OK</button>
            <button onClick={clickBack}>BACK</button>

          </div>
        </div>

         </div>
   }
   {
            yellow && back && show && <div>
          <div className='addMenu'>
            <textarea id='yellow' className='textAreaAdd'></textarea>
            <div className='flexContainer2'>
              <button id='gray' onClick={clickGray}></button>
              <button id='yellow' onClick={clickYellow}></button>
              <button id='blue'onClick={clickBlue}></button>
              <button id='pink' onClick={clickPink}></button>
              <button id='purple' onClick={clickPurple}></button>
              <button id='green'  onClick={clickGreen}></button>
          </div>
          <div className='flexContainer3'>
            <button onClick={ClickOkay}>OK</button>
            <button onClick={clickBack}>BACK</button>

          </div>
        </div>

         </div>
   }
   {
            blue && back && show && <div>
          <div className='addMenu'>
            <textarea id='blue' className='textAreaAdd'></textarea>
            <div className='flexContainer2'>
              <button id='gray' onClick={clickGray}></button>
              <button id='yellow' onClick={clickYellow}></button>
              <button id='blue' onClick={clickBlue}></button>
              <button id='pink' onClick={clickPink}></button>
              <button id='purple' onClick={clickPurple}></button>
              <button id='green' onClick={clickGreen}></button>
          </div>
          <div className='flexContainer3'>
            <button onClick={ClickOkay}>OK</button>
            <button onClick={clickBack}>BACK</button>

          </div>
        </div>

         </div>
   }
   {
            pink && back && show && <div>
          <div className='addMenu'>
            <textarea id='pink' className='textAreaAdd'></textarea>
            <div className='flexContainer2'>
              <button id='gray' onClick={clickGray}></button>
              <button id='yellow' onClick={clickYellow}></button>
              <button id='blue' onClick={clickBlue}></button>
              <button id='pink' onClick={clickPink}></button>
              <button id='purple' onClick={clickPurple}></button>
              <button id='green'  onClick={clickGreen}></button>
          </div>
          <div className='flexContainer3'>
            <button onClick={ClickOkay}>OK</button>
            <button onClick={clickBack}>BACK</button>

          </div>
        </div>

         </div>
   }
   {
            purple && back && show && <div>
          <div className='addMenu'>
            <textarea id='purple' className='textAreaAdd'></textarea>
            <div className='flexContainer2'>
              <button id='gray' onClick={clickGray}></button>
              <button id='yellow' onClick={clickYellow}></button>
              <button id='blue' onClick={clickBlue}></button>
              <button id='pink' onClick={clickPink}></button>
              <button id='purple' onClick={clickPurple}></button>
              <button id='green' onClick={clickGreen}></button>
          </div>
          <div className='flexContainer3'>
            <button onClick={ClickOkay}>OK</button>
            <button onClick={clickBack}>BACK</button>

          </div>
        </div>

         </div>
   }
   {
            green && back && show && <div>
          <div className='addMenu'>
            <textarea id='green' className='textAreaAdd'></textarea>
            <div className='flexContainer2'>
              <button id='gray' onClick={clickGray}></button>
              <button id='yellow' onClick={clickYellow}></button>
              <button id='blue' onClick={clickBlue}></button>
              <button id='pink' onClick={clickPink}></button>
              <button id='purple' onClick={clickPurple}></button>
              <button id='green' onClick={clickGreen}></button>
          </div>
          <div className='flexContainer3'>
            <button onClick={ClickOkay}>OK</button>
            <button onClick={clickBack}>BACK</button>

          </div>
        </div>

         </div>
   }

     <div className='MainBoard'>
        <div className='descriptionZone'>
          <p className='description'>Descripton of board:</p>
          <div><textarea className='textDescription'></textarea></div>
        </div>

    <div className='flexContainerSticky'></div>


      </div>
  </div>

  );
}

export default App;
