import './App.css';
import {Menu} from './Menu.js';
import {useState} from 'react';
//import {useRef} from 'react';
import { Colors } from './Colors';

const allStickies=new Array();
//const allBoards=new Array();
var indexId=1;

export function App() {

  const sticky=new Array();
  let currentSticky={id: null, text:"",color:"" ,select:"",vote:0};
  //var currentBoard={title:"",description:"",allStickyes:[]};

  const [show, setShow] = useState(false);
  const [back, setBack]=useState(true);
  const [okay, setOkay]=useState(true);

  const [edit, setEdit]=useState(false);
  const [editBack,setEditBack]= useState(true);
  const [editOk, setEdiitOk]=useState(true);

  const[save,setSave]=useState(false);

  const settings={
    show,
    setShow,
    edit,
    setEdit,
    save,
    setSave
  };

  function ClickBack() {
    setBack(back);
    setShow(!show);
  }
  function ClickOkay() {
      setOkay(okay);
      setShow(!show);

   currentSticky.id=indexId;
   indexId+=1;

    //???
   currentSticky.color="";
   currentSticky.text="";

   sticky.push(currentSticky);
   allStickies.push(sticky);
   console.log(sticky);
   console.log(allStickies);

   //render  stickies
   RenderSticky();

  }
  const RenderSticky = () => {
    for(let i=0; i <allStickies.length; i++)
    {
        return(
          <div className='sticky' style={{backgroundColor: allStickies[i].color}}>allStickies[i].text</div>
        );
    }
  }
  const  ClickEditBack = () => {
    setEditBack(editBack);
    setEdit(!edit);
  }
  const clickEditOkay = () => {
    setEdiitOk(editOk);
    setEdit(!edit);
  }

  return (

    <div className='App'>
     <Menu settings={settings}/>
     {
           back && show && <div>
          <div className='addMenu'>

           <Colors />

        </div>

         </div>
   }
   {
      edit && <div>
       <div className='editMenu'>
       <p className='editText'>Edit title:</p>
       <div><textarea></textarea></div>
       <p className='editText'>Edit description:</p>
       <div><textarea></textarea></div>
       <div className='flexContainer1'>
         <button onClick={clickEditOkay}>OK</button>
         <button onClick={ClickEditBack}>BACK</button>
       </div>
     </div>
     </div>
   }


     <div className='MainBoard'>
        <div className='descriptionZone'>
          <p className='description'>Descripton of board:</p>
          <div><textarea className='textDescription'></textarea></div>
        </div>

    <div className='flexContainerSticky'></div>


      </div>
  </div>

  );
}

export default App;
