import './App.css';
import {Menu} from './Menu.js';
import {useState} from 'react';
import { ColorsStickies } from './Colors';
import {useRef} from 'react';
//import { VoteSticky } from './Vote';
import * as React from 'react';

var indexId=1;
let myTitle;
let myDescription;
let currentColor;

export function App() {

  const [show, setShow]=useState(false);
  const [back, setBack]=useState(true);
  const [okay, setOkay]=useState(true);

  const [edit, setEdit]=useState(false);
  const [editBack,setEditBack]= useState(true);
  const [editOk, setEdiitOk]=useState(true);

  var[counter,setCounter]=useState(0);
  const updateVote=(item) => {
    counter=item.vote;
    setCounter(counter);
  }
  //!!!!
  const[editSticky,setEditSticky]=useState(false);
  var[currentSticky,setCurrentSticky]=useState({});
  const ref=useRef(null);
  const handleMessage = event => {
    let currentText=ref.current.value;
    currentSticky.text=currentText;
 }

  const newEditedSticky = (item) => {
   setEditSticky(!editSticky);

   const newSticky=stickies.map(current => {
      if(current.id===item.id){
        return{...current, id:item.id, text:item.text, color:item.color, select:item.select, vote:item.vote}
      }
      return current;
  });
    //setStickies(newSticky);
    //console.log(newSticky);
  }

  const[stickies,setStickies]=useState([]);
  const addSticky=(sticky) => {
   var newStickies=[...stickies,sticky];
    setStickies(newStickies);
  }

 const[changeDesc,setChangeDesc]= useState();
 const ref1=useRef(null);
 let newDescription;

 const getDescription = event => {
    newDescription=ref1.current.value;
 }
 const ref4=useRef(null);
  const changeDescription = () => {
    setChangeDesc(newDescription);
    myDescription=ref4.current.value;
  }

  const[changeTitle,setChangeTitle]=useState();
  const ref2=useRef(null);
  let newTitle;

  const getTitle = event => {
    newTitle=ref2.current.value;
  }
  const ref3=useRef(null);
  const changeNewTitle = () => {
    setChangeTitle(newTitle);
    myTitle=ref3.current.value;
  }

  const[checked, setChecked]=React.useState(false);

  const handleChange = () => {
    setChecked(!checked);
    console.log(checked);
  }
  const checkSticky = (item) => {
    const nextStickies=stickies.map(current => {
      if(current.id===item.id){
        return{...item, select: !item.select}
      }
      return current;
  });
    setStickies(nextStickies);
    console.log(nextStickies);
  }

const[boards,setBoards]=useState([]);
const addBoard=(board)=> {
  var newBoard=[...boards,board];
  setBoards(newBoard);
}

const[allBoards,setAllBoards]= useState(false);
const viewBoards= () => {
  setAllBoards(!allBoards);
}

  const settings={
    show,
    setShow,
    edit,
    setEdit,
    editSticky,
    setEditSticky,
    stickies,
    setStickies,
    boards,
    setBoards,
    addBoard,
    myTitle,
    myDescription,
    viewBoards

  };

  const ClickBack = () => {
    setBack(back);
    setShow(!show);
  }

  const ClickOkay=(currentSticky) => {
      setOkay(okay);
      setShow(!show);

   currentSticky.id=indexId;
   currentSticky.select=false;
   currentSticky.vote=0;
   indexId+=1;

   stickies.push(currentSticky);
   //setStickies(currentSticky); //No work!
   console.log(stickies);
  }

  const  ClickEditBack = () => {
    setEditBack(editBack);
    setEdit(!edit);
  }
  const clickEditOkay = () => {
    setEdiitOk(editOk);
    setEdit(!edit);

    changeDescription();
    changeNewTitle();
  }

  const increase=(item) => {
    item.vote=item.vote+1;;
    updateVote(item);
   //console.log(stickies);
  }
  const decrease = (item) => {
    if(item.vote>0)
    {
        item.vote=item.vote-1;
        updateVote(item);
      //console.log(stickies);
    }

}

  return (

    <div className='App'>
     <Menu settings={settings}/>
     {
           back && show && <div>
          <div className='addMenu'>

           <ColorsStickies funcOk={ClickOkay} funcBack={ClickBack}/>

        </div>
         </div>
    }
    {
      edit && <div>
       <div className='editMenu'>
       <p className='editText'>Edit title:</p>
       <div><textarea ref={ref2} onChange={getTitle}></textarea></div>
       <p className='editText'>Edit description:</p>
       <div><textarea ref={ref1} onChange={getDescription}></textarea></div>
       <div className='flexContainer1'>
         <button onClick={clickEditOkay}>OK</button>
         <button onClick={ClickEditBack}>BACK</button>
       </div>
     </div>
     </div>
   }

     <div className='MainBoard'>
      <div className='flex'>
     <div className='titleBox'>
            <p className='labelTitile'>Title of board:</p>
            <textarea className='titleText' ref={ref3} value={changeTitle} onChange={changeNewTitle}  ></textarea>
      </div>
      <div className='descriptionZone'>
          <p className='description'>Descripton of board:</p>
          <div><textarea className='textDescription' ref={ref4} value={changeDesc} onChange={changeDescription}></textarea></div>
      </div>
      </div>

 <div className='flexContainerSticky'> 
      {
            stickies && stickies.map(item => 

                <div id={item.color}key={item.id}>
                <div id={item.color}>{item.text}</div>

              <div className='voteMenu'>
              <p className='counter'>{item.vote}</p>
              <button className='updateMinus' onClick={() => {decrease(item)}}>-</button>
              <button className='updatePlus' onClick={() => {increase(item)}}>+</button>
              </div>

                <input className='selectBox' type='checkbox' value={item.select} onChange={() => {checkSticky(item)}} onClick={handleChange}/>

               <div>
                {stickies.map(item => {
                  if(item.select)
                  {
                  currentSticky=item;
                  currentColor=currentSticky.color;
                  //setCurrentSticky({...currentSticky, id:item.id, text:item.text, color: item.color, select:item.select, vote:item.vote});
                  //setCurrentSticky({...currentSticky, item});
                  console.log(currentSticky);
                  }
                })}
               </div>

              </div> 
            )
      }

      {
          allBoards && boards.map(currentBoard =>
            <div id='finish'key={currentBoard.title}>
            <div>{currentBoard.title}</div>
            <div>{currentBoard.description}</div>
            </div>
            )
      }
       </div>

       {
        editSticky && currentSticky &&
        <div className='editApp'>
        <textarea className='textAreaAdd2' style={{backgroundColor: currentSticky.color}} value={currentSticky.text} ref={ref4} onChange={handleMessage}  ></textarea>
        <div className='flexContainer2'>
         <button id='gray' onClick={()=> {setCurrentSticky(currentSticky.color='gray')}}></button>
         <button id='yellow' onClick={() => {setCurrentSticky(currentSticky.color='yellow')}}></button>
         <button id='aqua' onClick={() => {setCurrentSticky(currentSticky.color='aqua')}}></button>
         <button id='pink' onClick={() => {setCurrentSticky(currentSticky.color='pink')}}></button>
         <button id='purple' onClick={() => {setCurrentSticky(currentSticky.color='purple')}}></button>
         <button id='green' onClick={() => {setCurrentSticky(currentSticky.color='green')}}></button>

         <div className='flexContainer3'>
         <button /*onClick={newEditedSticky(currentSticky)}*/>OK</button>
         <button onClick={setEditSticky(!editSticky)}>BACK</button>
       </div>
