import {App} from'./App.js';
import './App.css';
import {useRef} from 'react';

export function Menu({settings})
{
  const createButton = () => {
    settings.setShow(!settings.show);
  }
  const editButton = () => {
    settings.setEdit(!settings.edit);
  }
  const isEditSticky = () =>{
    settings.setEditSticky(!settings.editSticky);

  }
  const deleteButton= () => {
    settings.setDeleteSticky(item =>
      item.filter(settings.deleteStiicky));
  }
  const saveCurrentBoard=(title,description,sticky) => {

   // current.title=
    //current.description=
    //current.sticky=[...settings.saveBoard,sticky];

    //settings.saveBoard.push(current);
    settings.setBoards(!settings.boards);
    //console.log(settings.saveBoard);


    //clear all
}

    return(
        <div className='Menu'>
         <label className='menuLabel'>Menu</label>
          <div className='menu1'>
            <label className='options'>Board Options:</label> 
            <button id='finish' onClick={editButton}>Edit Board</button>
            <button>View Boards</button>
            <button id='no' onClick={saveCurrentBoard}>Save Boards</button>
          </div>
          <div className='menu2'>
            <label className='options'>Task options:</label>

            <button id='finish' onClick={createButton}>Create Task</button>

            <button id='no' onClick={isEditSticky}> Edit Task</button>
            <button onClick={deleteButton}>Delete Task</button>
            <button>Import Task</button>
            <button>Export Task</button>
            <button>Group Task by color</button>
          </div>
      </div>
      );
}
