       let currentText=ref.current.value;
        return currentText;
    }

return(
    <div>
        <textarea className='textAreaAdd' style={{backgroundColor:currentColor}} ref={ref} onChange={handleMessage}></textarea>
        <div className='flexContainer2'>
            <button id='gray' onClick={() => {setCurrentColor('gray')}} ></button>
