import './App.css';
import {useState} from 'react';
import {useRef} from 'react';

export const ColorsStickies = (props) => {

    const ref=useRef(null);
    const[currentSticky,setCurrentSticky]=useState({color:'gray'});

    const handleMessage = event => {
       let currentText=ref.current.value;
       currentSticky.text=currentText;

    }

return(
    <div>
        <textarea className='textAreaAdd' style={{backgroundColor: currentSticky.color}} ref={ref} onChange={handleMessage}></textarea>
        <div className='flexContainer2'>
            <button id='gray' onClick={() =>   {setCurrentSticky({...currentSticky, color: 'gray'})}} ></button>
            <button id='yellow' onClick={() => {setCurrentSticky({...currentSticky, color: 'yellow'})}}></button>
            <button id='aqua' onClick={() => {setCurrentSticky({...currentSticky, color: 'aqua'})}}></button>
            <button id='pink' onClick={() => {setCurrentSticky({...currentSticky, color: 'pink'})}}></button>
            <button id='purple' onClick={() => {setCurrentSticky({...currentSticky, color:'purple'})}}></button>
            <button id='green' onClick={() => {setCurrentSticky({...currentSticky,color:'green'})}}></button>

            <div className='flexContainer3'>
            <button onClick={()=> props.funcOk(currentSticky)}>OK</button>
            <button onClick={props.funcBack}>BACK</button>
          </div>
        </div>
    </div>

);

}
