import {useState} from "react";
import './App.js';
import './App.css';
//import Popup from 'reactjs-popup';

export  function  Menu(){

  const [display, isDisplayed] = useState(false);

  function create() {
      isClicked(!display);
  }

    return(
    <div className='Menu'>
         <label className='menuLabel'>Menu</label>
          <div className='titleBox'>
            <label className='labelTitile'>Title:</label>
            <div><textarea className='titleText'> </textarea></div>
          </div>
          <div className='menu1'>
            <label className='options'>Board Options:</label>
            <button>Edit Board</button>
            <button>View Boards</button>
            <button>Save Boards</button>
          </div>
          <div className='menu2'>
            <label className='options'>Task options:</label>

            <button onClick={create}>Create Task</button>
            document.getElementsByClassName('addMenu') ={{ display: isClicked ? "grid" : "none" }};

            <button>Edit Task</button>
            <button>Delete Task</button>
            <button>Import Task</button>
            <button>Export Tadsk</button>
            <button>Select Tasks</button>
            <button>Group Task by color</button>
          </div>
      </div>
      );
}
